| CREATE OR REPLACE FUNCTION public.get_public_page_data(p_username text)
 RETURNS json
 LANGUAGE plpgsql
 STABLE
AS $function$
DECLARE
    v_user_id UUID;
    v_page_id UUID;
    v_result JSON;
BEGIN
    -- Buscar user_id pelo username
    SELECT user_id INTO v_user_id
    FROM usernames
    WHERE username = p_username;
    
    IF v_user_id IS NULL THEN
        RETURN json_build_object('error', 'Página não encontrada');
    END IF;
    
    -- Buscar page_id primária
    SELECT id INTO v_page_id
    FROM pages
    WHERE user_id = v_user_id 
    AND is_primary = true
    AND is_active = true
    LIMIT 1;
    
    IF v_page_id IS NULL THEN
        RETURN json_build_object('error', 'Página não encontrada');
    END IF;
    
    -- Construir resposta completa em JSON
    SELECT json_build_object(
        'page', (
            SELECT row_to_json(p.*)
            FROM pages p
            WHERE p.id = v_page_id
        ),
        'settings', (
            SELECT row_to_json(ps.*)
            FROM page_settings ps
            WHERE ps.page_id = v_page_id
        ),
        'resources', COALESCE((
            SELECT json_agg(
                json_build_object(
                    'id', r.id,
                    'type', r.type,
                    'title', r.title,
                    'display_order', r.display_order,
                    'is_visible', r.is_visible,
                    
                    -- MANTIDO: link_data (igual ao original)
                    'link_data', CASE 
                        WHEN r.type IN ('link', 'youtube', 'spotify') THEN (
                            SELECT row_to_json(l.*)
                            FROM links l
                            WHERE l.resource_id = r.id
                        )
                        ELSE NULL
                    END,
                    
                    -- MANTIDO: image_banner_data (igual ao original)
                    'image_banner_data', CASE 
                        WHEN r.type = 'image_banner' THEN (
                            SELECT row_to_json(ib.*)
                            FROM image_banners ib
                            WHERE ib.resource_id = r.id
                        )
                        ELSE NULL
                    END,
                    
                    -- ALTERADO: whatsapp_data (agora busca de links)
                    'whatsapp_data', CASE
                        WHEN r.type = 'whatsapp' THEN (
                            SELECT json_build_object(
                                'phone_number', SUBSTRING(l.url FROM 'wa\.me/([0-9]+)'),
                                'message', NULL,
                                'image_url', l.image_url,
                                'bg_color', l.bg_color
                            )
                            FROM links l
                            WHERE l.resource_id = r.id
                        )
                        ELSE NULL
                    END,
                    
                    -- MANTIDO: gallery_data (igual ao original)
                    'gallery_data', CASE
                        WHEN r.type = 'gallery' THEN (
                            SELECT json_build_object(
                                'gallery', row_to_json(g.*),
                                'items', COALESCE((
                                    SELECT json_agg(row_to_json(gi.*) ORDER BY gi.display_order)
                                    FROM gallery_items gi
                                    WHERE gi.gallery_id = g.id
                                ), '[]'::json)
                            )
                            FROM galleries g
                            WHERE g.resource_id = r.id
                        )
                        ELSE NULL
                    END,
                    
                    -- MANTIDO: form_data (igual ao original)
                    'form_data', CASE
                        WHEN r.type = 'form' THEN (
                            SELECT row_to_json(f.*)
                            FROM forms f
                            WHERE f.resource_id = r.id
                        )
                        ELSE NULL
                    END
                ) ORDER BY r.display_order
            )
            FROM resources r
            WHERE r.page_id = v_page_id
            AND r.is_visible = true
        ), '[]'::json),
        
        -- MANTIDO: social_networks (igual ao original)
        'social_networks', COALESCE((
            SELECT json_agg(row_to_json(sn.*) ORDER BY sn.display_order)
            FROM social_networks sn
            WHERE sn.page_id = v_page_id
        ), '[]'::json)
    ) INTO v_result;
    
    RETURN v_result;
END;
$function$
